class Animals:    color = " "    legCount = 0    ageCount = 0    def __init__(self, color=" ", legCount=0, ageCount=0):        self.color = color        self.legCount = legCount        self.ageCount = ageCount    def printInfo(self):        print(f'The animal info is, {vars(self)} ')class Reptile(Animals):    def printInfo(self):        print(f'The reptile info is, {vars(self)} ')class Bird(Animals):    def __init__(self, color=" ", legCount=0, ageCount=0, wingCount=0):        super().__init__(color, legCount, ageCount)        self.wingCount = wingCount    def printInfo(self):        print(f'The bird info is, {vars(self)} ')animals = Animals("Brown ", 4, 15)reptile = Reptile("Green ", 4, 20)bird = Bird("Blue ", 2, 5, 2)animals.printInfo()reptile.printInfo()bird.printInfo()