def periodTime(time):    return time * 60class Class:    name = ". "    studentsCount = 0    teachers = ". "    windowsCount = 0    shelvesCount = 0    schoolStartsAt = ". "    def __init__(self, name=". ", studentsCount=0, teachers=". ", windowsCount=0, shelvesCount=0, schoolStartsAt=". "):        self.name = name        self.studentsCount = studentsCount        self.teachers = teachers        self.windowsCount = windowsCount        self.shelvesCount = shelvesCount        self.schoolStartsAt = schoolStartsAt    def printInfo(self):        print("This class is, ", vars(self))class1 = Class("Class 1 ", 20, "Mrs. Wells ", 0, 7, "8:00 ")class1.printInfo()print("The class is, ", {periodTime(1.5)}, "Minutes. ")print("------------------------------------------------------------------------------------------------------------- ")class2 = Class("Class 2. ", 23, "Mrs. Jones. ", 4, 2, "8:00. ")class2.printInfo()print("The class is, ", {periodTime(1.0)}, "Minutes. ")