class Bird:    wingCount = 0    legCount = 0    age = 0    color = "None "    def __init__(self, wingCount=0, legCount=0, age=0, color="None "):        self.wingCount = wingCount        self.legCount = legCount        self.age = age        self.color = color    def printInfo(self):        print(f'the Bird info is, {vars(self)} ')class Eagle(Bird):    def __init__(self, wingCount=0, legCount=0, age=0, color="None ", multiColor="None " "None "):        super().__init__(wingCount, legCount, age, color)        self.multiColor = multiColor    def printInfo(self):        print(f'the Eagle info is, {vars(self)} ')class Hawk(Bird):    def printInfo(self):        print(f'the Hawk info is, {vars(self)} ')class Falcon(Bird):    def printInfo(self):        print(f'the Falcon info is, {vars(self)} ')bird = Bird(2, 2, 4, "Black ")eagle = Eagle(2, 2, 4, "It is multi colored", "Black " "White")hawk = Hawk(2, 2, 4, "Black ")falcon = Falcon(2, 2, 4, "Black ")bird.printInfo()eagle.printInfo()hawk.printInfo()falcon.printInfo()