# https://www.hackerrank.com/challenges/text-wrap/problem# Output Format## Print the text wrapped paragraph.## Sample Input 0## ABCDEFGHIJKLIMNOQRSTUVWXYZ# 4# Sample Output 0## ABCD# EFGH# IJKL# IMNO# QRST# UVWX# YZdef wrap(string, max_width):    s = ""    for c in range(0, len(string), max_width):        s += string[c:c + max_width] + "\n"    return sif __name__ == '__main__':    string, max_width = input(), int(input())    result = wrap(string, max_width)    print(result)